// Function node (normalize Actions into clean JSON structure)

let raw = $('Loop Over Cases').first().json.Actions;

// If it's a string, try to parse it
if (typeof raw === 'string') {
  try {
    raw = JSON.parse(raw);
  } catch (err) {
    raw = raw
      .replace(/^\[|\]$/g, '')
      .split(',')
      .map(s => s.trim())
      .filter(Boolean);
  }
}

// Ensure array
if (!Array.isArray(raw)) {
  raw = raw ? [raw] : [];
}

// Normalize actions (remove duplicates, trim spaces)
let actions = [...new Set(raw.map(a => String(a).trim()))];

// Remove 'create_ticket'
actions = actions.filter(a => a !== 'create_ticket');

// Clean: remove empty values
actions = actions.filter(a => a.length > 0);

// Return in normalized schema
return {
  json: {
    Actions: actions
  }
};

// Take IOCs field from model output and normalize

let raw = $('When Executed by Another Workflow').first().json.IOCs

// If it's a string, try to parse it
if (typeof raw === "string") {
  try {
    raw = JSON.parse(raw);
  } catch (err) {
    raw = {}; // fallback
  }
}

// If it's still not an object, force empty object
if (typeof raw !== "object" || raw === null) {
  raw = {};
}

// Normalize to standard IOC schema
const iocs = {
  alert_id: raw.alert_id || $json.alert_id || null,
  user: raw.user_name || raw.user || raw.target_account || raw.device  || null,
  source_ip: raw.source_ip || null,
  source_hostname: raw.source_hostname || raw.destination_hostname,
  process: raw.source_process_name || raw.process || raw.parent_process_name || raw.parent_process || null,
  cmdline: raw.cmd_line || raw.cmdline || null,
  file: raw.file_name || raw.file || null,
  hash: raw.file_hash || raw.hash || null
};

// Clean: remove null/empty fields
const cleaned = Object.fromEntries(
  Object.entries(iocs).filter(([_, v]) => v !== null && v !== "")
);

return { json: { IOCs: cleaned } };

