// Step 1: Extract raw model output
const msg = $input.first().json.content.parts[0].text;

// Step 2: Match JSON inside ```json ... ```
const regex = /```json([\s\S]*?)```/;
const match = msg.match(regex);

let parsed;

if (match) {
  try {
    parsed = JSON.parse(match[1]);
  } catch (err) {
    parsed = { "chat_output": msg, "error": "Failed to parse JSON", "details": err.message };
  }
} else {
  parsed = { "chat_output": msg };
}

// Step 3: Build email subject if report data exists
let subject = "Phishing Investigation Report";

if (parsed?.report?.verdict && parsed?.report?.email_analysis) {
  // Extract subject line from email_analysis (after "Subject:")
  let emailSubject = "";
  const subjectMatch = parsed.report.email_analysis.match(/Subject:\s*([^\n]+)/i);
  if (subjectMatch) {
    emailSubject = subjectMatch[1].trim();
  }

  subject = `Phishing Investigation Report - ${parsed.report.verdict} - ${emailSubject}`;
}

// Step 4: Convert parsed.report into HTML table for email body
let htmlBody = "<p>No report data available.</p>";

if (parsed?.report) {
  htmlBody = "<table border='1' cellpadding='5' cellspacing='0' style='border-collapse: collapse;'>";
  htmlBody += "<tr><th>Field</th><th>Value</th></tr>";

  for (const [key, value] of Object.entries(parsed.report)) {
    let displayValue = value;

    // If value is an object or array, stringify it for HTML table
    if (typeof value === 'object') {
      displayValue = JSON.stringify(value, null, 2)
        .replace(/\n/g, '<br>')      // preserve line breaks
        .replace(/ /g, '&nbsp;');    // preserve spaces
    }

    htmlBody += `<tr><td>${key}</td><td>${displayValue}</td></tr>`;
  }

  htmlBody += "</table>";
}

// Step 5: Return parsed JSON unchanged + email subject + HTML body
return {
  json: {
    ...parsed,           // keep original body
    subject: subject,    // updated email subject
    email_body: htmlBody,
    raw_output: msg
  }
};

// Input: Any.Run node output with structure { data: { tasks: [...] } }
// Output: Flattened raw data string

const firstTask = $input.first().json.data.tasks?.[0] || $input.first().json.data.analysis;

const verdict = firstTask.verdict || 'N/A';
const tags = Array.isArray(firstTask.tags) ? firstTask.tags.join(', ') : 'N/A';

const rawData = `Verdict: ${verdict}\nTags: ${tags}`;

return [{ json: { rawData } }];
