const raw = $input.first().json.output; // AI Agent output
let parsed;

try {
  parsed = JSON.parse(raw);
} catch (e) {
  throw new Error("AI output is not valid JSON: " + e.message);
}

// Extract links from articles array
const urls = parsed.articles
  ? parsed.articles.map(article => article.link).filter(Boolean)
  : [];

/* -------------------- Return Only URLs -------------------- */
return [{
  json: {
    urls: urls
  }
}];

const raw = $input.first().json.output || "";

// --- Normalize CSV text ---
let csv = raw
  .replace(/^\s*```(?:csv)?/i, "")
  .replace(/```$/i, "")
  .replace(/```/g, "")
  .replace(/\r\n/g, "\n")
  .replace(/\r/g, "\n")
  .trim();

const lines = csv.split("\n").map(l => l.trim()).filter(l => l.length);
if (lines.length < 2) throw new Error("No IOC data found.");

// --- Parse CSV rows ---
const rows = lines.slice(1).map(line => {
  const cols = line.split(",");
  return {
    type: (cols[0] || "").trim(),
    value: (cols[1] || "").trim(),
    first_seen: (cols[2] || "").trim(),
    source: (cols[3] || "").trim(),
    confidence: (cols[4] || "").trim(),
    mitre: (cols[5] || "").trim(),
    description: (cols[6] || "").trim() // optional column
  };
}).filter(r => r.value);

// --- Helper for SPL string escaping ---
const esc = (s) => (s || "").replace(/"/g, '\\"');

// --- Build search query (index search for IOCs) ---
const values = rows.map(r => r.value).filter(Boolean);
if (values.length === 0) throw new Error("No IOC values found.");

const evalCase = values.map(v => `searchmatch("${esc(v)}"), "${esc(v)}"`).join(", ");
const splunkSearchQuery =
  `index=* (${values.map(v => `"${esc(v)}"`).join(" OR ")})` +
  ` | eval IOC=case(${evalCase}, true(), null())` +
  ` | lookup threat_hunt_iocs.csv value as IOC OUTPUT type, source, confidence, mitre_technique, first_seen` +
  ` | stats values(type) as IOC_Type values(source) as Source values(confidence) as Confidence values(mitre_technique) as MITRE values(host) as Matching_Hosts values(sourcetype) as SourceType values(index) as Index by IOC` +
  ` | table IOC IOC_Type Source Confidence MITRE Matching_Hosts SourceType Index`;

// --- Build lookup update query ---
let parts = [];
rows.forEach((r, idx) => {
  const base = idx === 0 ? "| makeresults" : "| append [| makeresults]";
  const evalStr =
    `${base} | eval ` +
    `type="${esc(r.type)}", ` +
    `value="${esc(r.value)}", ` +
    `first_seen="${esc(r.first_seen)}", ` +
    `source="${esc(r.source)}", ` +
    `confidence="${esc(r.confidence)}", ` +
    `mitre_technique="${esc(r.mitre)}", ` +
    `description="${esc(r.description)}"`;
  parts.push(evalStr);
});

const splunkLookupQuery =
  parts.join(" ") + " | outputlookup append=true threat_hunt_iocs.csv";

// --- Return both ---
return [{
  json: {
    splunkSearchQuery,
    splunkLookupQuery
  }
}];

const data = $input.first().json.data; // your incoming JSON object/array

// --- Escape unsafe XML/CSV characters ---
function sanitizeValue(val) {
  if (val === null || val === undefined) return "";
  if (typeof val !== "string") val = String(val);
  return val
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, '\\"')
    .replace(/'/g, "\\'");
}

// --- Convert JSON to CSV-like raw text ---
function jsonToRaw(json) {
  let rows = [];

  if (Array.isArray(json)) {
    // Handle array of objects
    json.forEach(obj => {
      let row = [];
      for (let key in obj) {
        row.push(`${key}=${sanitizeValue(obj[key])}`);
      }
      rows.push(row.join(", "));
    });
  } else if (typeof json === "object") {
    // Handle single object
    let row = [];
    for (let key in json) {
      row.push(`${key}=${sanitizeValue(json[key])}`);
    }
    rows.push(row.join(", "));
  }

  return rows.join("\n");
}

return {
  rawData: jsonToRaw(data)
};
